import React, { useEffect, useState } from 'react';
import axios  from 'axios';
import { Layout } from '../components/Layout'

const BASE_URL ='https://simple-blog-api.crew.red/posts'

export default function Posts() {
    const [ posts, setPosts ] = useState ([]);

    useEffect(() => {
        axios.get(BASE_URL)
            .then(function (response) {
                setPosts(response.data.reverse());
            })
            .catch(function (error) {
                console.log(error);
            })
            .finally(function () {

            });
    }, []);

    const handleClickEdit = (id) => {
        console.log(id)
    };

    const handleClickDelete = (id) => {
        axios.delete(`${BASE_URL}/${id}`);
        setPosts(posts.filter(post => post.id !== id));
    };

    return (
        <Layout>
            <ul>
                {posts.map(post => (
                <li key={`${post.id}-${post.title} `}>
                    <div className="card">
                        <div className="card-header">
                            <div className="card-title">{`#${post.id} ${post.title}`}</div>
                            <button type="button" onClick={handleClickEdit}className="btn-edit">
                                Edit
                            </button>
                            <button type="button" onClick={() => handleClickDelete(post.id)}className="btn-delete">
                                Delete
                            </button>
                        </div>
                        <div className="card-body">{post.body}</div>
                    </div>
                </li>
                ))}
            </ul>
        </Layout>
    );
}
