import React, { useState } from 'react';
import { v4 } from 'uuid';
import axios from 'axios';
import { Modal  } from '../components/Modal';
import { Layout  } from '../components/Layout';
import { Form  } from '../components/Form';
import { Card  } from '../components/Card';
import {modal} from "../components/Modal/ModalStyles";

const BASE_URL ='https://simple-blog-api.crew.red/posts';

export default function New() {
    const[isModalOpen, setIsModalOpen] = useState(false);

    const setNewPost = async (post) => {
        await axios({
            method: 'post',
            url: `${BASE_URL}`,
            data: post,
        });
    };

    const closeModal = () => {
        setIsModalOpen(false);
    }

    return (
        <Layout>
            <Card title="Create new post now!">
                <Form onSubmitForm={setNewPost} />
            </Card>
            {isModalOpen &&
                <Modal onClose={closeModal}>
                  <p>Success!!!</p>
                  <p>The post was successfully added!</p>
                </Modal>
            }
        </Layout>
    );
}
