import React, { FC } from 'react';
import { useRouter } from "next/router";
import Link from 'next/link';
import { modal } from './ModalStyles';

interface Props {
    onClose(): void;
}

export const Modal:FC<Props> = ({ onClose }) => {
    const handleClose = () => {
        onClose();
    }

    return (
        <modal.Backdrop>
            <modal.Wrapper>
                <modal.BtnClose type="button" onClick={handleClose}>Close</modal.BtnClose>
                <modal.Content>
                    <p>Success!!!</p>
                    <p>The post was successfully added!</p>
                </modal.Content>
            </modal.Wrapper>
        </modal.Backdrop>
    );
}
