import styled from 'styled-components';

export const s = {
    List: styled.div `
        display: flex;
        flex-wrap: wrap;
        justify-content: space-between;
    `,

    Item: styled.div `
        flex-basis: 48%;
        width: 48%;
        position: relative;
        box-sizing: border-box;
        margin: 0;
        margin-bottom: 26px;
    
    &:after {
        content: "";
        position: absolute;
        bottom: 18px;
        left: 5px;
        width: 50%;
        height: 30px;
        z-index: 0;
        max-width: 300px;
        box-shadow: 0 20px 5px rgba(0, 0, 0, 0.7);
        transform: rotate(-4deg);
        }
    
    &:before {
        content: "";
        position: absolute;
        bottom: 18px;
        left: 5px;
        width: 50%;
        height: 30px;
        z-index: 0;
        max-width: 300px;
        box-shadow: 0 20px 5px rgba(0, 0, 0, 0.7);
        transform: rotate(-4deg);
        }
`,

    ItemWrapper: styled.div `
        width: 100%;
        height: 100%;
        background: #fff;
        padding: 20px 10px 70px;
        position: relative;
        z-index: 1;
    `
};
