import {
  SORT_BY_ID,
  SORT_BY_USER,
  SORT_BY_COMPLETED,
  SET_TODOS,
  SET_IS_LOADING,
  SORT_BY_TITLE,
  SET_IS_LOADED,
  REMOVE_TODO,
} from './actionCreators';

const initialState = {
  todos: [],
  isLoading: false,
  isLoaded: false,
};

export const reducer = (state: TodosState = initialState, action: TodosAction) => {
  switch (action.type) {
    case SET_TODOS:
      return {
        ...state,
        todos: action.todos,
      };
    case SET_IS_LOADING:
      return {
        ...state,
        isLoading: !state.isLoading,
      };
    case SET_IS_LOADED:
      return {
        ...state,
        isLoaded: !state.isLoaded,
      };
    case SORT_BY_TITLE:
      if (action.todos.length) {
        return {
          ...state,
          todos: [...state.todos]
            .sort((a, b) => a.title.localeCompare(b.title)),
        };
      }

      return state;
    case SORT_BY_ID:
      if (action.todos.length) {
        return {
          ...state,
          todos: [...state.todos]
            .sort((a, b) => a.id - b.id),
        };
      }

      return state;
    case SORT_BY_USER:
      if (action.todos.length) {
        return {
          ...state,
          todos: [...state.todos]
            .sort((a, b) => {
                return a.user.name.localeCompare(b.user.name);
            }),
        };
      }

      return state;
    case SORT_BY_COMPLETED:
      if (action.todos.length) {
        return {
          ...state,
          todos: [...state.todos]
            .sort((a, b) => {
              return Number(a.completed) - Number(b.completed);
            }),
        };
      }

      return state;
    case REMOVE_TODO:
      return {
        ...state,
        todos: state.todos.filter(todo => todo.id !== action.id),
      };
    default:
      return state;
  }
};
