import styled from 'styled-components';

export const card = {
    Item: styled.div `
        min-width: 500px;
        max-width: 100%;
        position: relative;
        box-sizing: border-box;
        margin: 0;
    
    &:after,
    &:before {
        content: "";
        position: absolute;
        bottom: 18px;
        left: 5px;
        width: 50%;
        height: 30px;
        z-index: 0;
        max-width: 300px;
        box-shadow: 0 20px 5px rgba(0, 0, 0, 0.3);
        transform: rotate(-3deg);
        }
        
    &:after {
        right: 5px;
        left: auto;
        transform: rotate(3deg);
        }
    `,

    ItemWrapper: styled.div `
        box-sizing: border-box;
        width: 100%;
        height: 100%;
        background: white;
        padding: 20px;
        box-shadow: 0 0 70px rgba(0, 0, 0, 0.1) inset;
        position: relative;
        z-index: 1;
        overflow: hidden;
        text-overflow: ellipsis;
    `,

    List: styled.div `
        display: grid;
        grid-template-columns: repeat(2, 500px);
        grid-gap: 20px;
    `,

    PostBody: styled.p `
        font-family: 'Raleway', sans-serif;
        overflow: hidden;
        text-overflow: ellipsis;
    `,

    Title: styled.h2 `
        font-family: "Comfortaa",sans-serif;
        text-align: center;
        line-height: 1.2;
        color: #f3969a;
        font-size: 20px;
        font-weight: 700;
        padding-bottom: 10px;
        position: relative;
        overflow: hidden;
        text-overflow: ellipsis;
        
        &:before {
            content: '';
            width: 100%;
            position: absolute;
            height: 1px;
            background: linear-gradient(to right, rgba(255,255,255,0)
                0%,rgba(243, 150, 154,1) 
                49%, rgba(255,255,255,0) 100%);
            bottom: 0px;
            left: 0;
            right: 0;
            z-index: 1;
        }
    `,
};

export const errorMess = {
    TitleError: styled(card.Title) `
        font-size: 14px;
    `,
}

export const newPost = {
    Input: styled.input `
        box-sizing: border-box;
        width: 100%;
        display: block;
        line-height: 40px;
        border: none;
        background: transparent;
        font-size: 14px;
        padding: 0 12px;
        font-family: "Raleway", sans-serif;
        color: rgba (0, 0, 0, 0.4);
        border-bottom: 2px solid #f3969a;
        
        &:placeholder-shown {
            font-family: "Raleway", sans-serif;
            color: rgba (0, 0, 0, 0.4);
        }
        
         &:focus {
            outline: none;
        }
    `,

    Textarea: styled.textarea `
        box-sizing: border-box;
        width: 100%;
        height: 150px;
        display: block;
        line-height: 1.2;
        background: transparent;
        font-size: 14px;
        padding: 12px;
        font-family: "Raleway", sans-serif;
        color: rgba (0, 0, 0, 0.4);
        border: 2px solid #f3969a;
        
        &:placeholder-shown {
            font-family: "Raleway", sans-serif;
            color: rgba (0, 0, 0, 0.4);
        }
        
         &:focus {
            outline: none;
        }
    `,

    Label: styled.label`
        display: block;
        box-sizing: border-box;
        margin-bottom: 20px;
    `,

    Button: styled.button `
       border: 2px solid #f3969a;
        box-sizing: border-box;
        width: 100%;
        display: block;
        line-height: 40px;
        background: #f3969a;
        color: #fff;
        font-family: "Comfortaa", sans-serif;
        text-transform: uppercase;
        transition: background-color 0.3s ease-in-out;
        
        &:hover {
            background: rgba(243, 150, 154, 0.6) ;
        }
    `,
}
