import styled from 'styled-components';

export const modal = {
    Backdrop: styled.div `
        position: fixed;
        left: 0;
        right: 0;
        top: 0;
        bottom: 0;
        z-index: 999;
        background-color: rgba(240, 246, 252, 0.5) ;
        backdrop-filter: blur(4px);
        display: flex;
        align-items: center;
        justify-content: center;
`,
    Wrapper: styled.div`
        min-width: 400px;
        padding-top: 16px;
        position: relative;
        background:  #f3969a;
    
        @media (max-width: 600px) {
          min-width: 100%;
        }
    `,
    Content: styled.div`
        box-shadow: 0 2px 4px rgba(100, 100, 100, 0.5);
        padding-bottom: 25px;
    `,
    BtnClose: styled.a`
        display: block;
        padding: 0.5rem 1rem;
        color: #fff;
        text-decoration: none;
        background-color: transparent;
    `,
};
