import React from 'react';
import { NextPage, NextPageContext } from 'next';
import axios from 'axios';
import { Layout } from "../../components/Layout";
import { Card } from "../../components/Card";

interface Post {
    id: string|number;
    title: string;
    body: string;
    comments?: string[];
}


interface Props {
    post: Post;
}

const Post:NextPage<Props> = ({ post }) => {
    return (
        <Layout>
            <h1>{post.id}</h1>
            <Card title={post.title} isLink={false}>
                <div>{post.body}</div>

                <ul>
                    {post.comments.map(item => (<li>{item}</li>))}
                </ul>
            </Card>
        </Layout>
    );
};

Post.getInitialProps = async (context: NextPageContext) => {
    console.log(context)
    const { id } = context.query;
    const postWithComments = await axios.get(`https://simple-blog-api.crew.red/posts/${id}?_embed=comments`);
    const post = postWithComments.data;

    return {
        post,
    };
};

export default Post;
