import React, { useState } from 'react';
import { v4 } from 'uuid';
import axios from 'axios';
import { Layout  } from '../components/Layout';
import { s } from './styles/PostStyles';

const BASE_URL ='https://simple-blog-api.crew.red/posts';

export default function New() {
    const[post, setPost] = useState({ id: 0, title: '', body: ''})
    const[error, setError] = useState('')

    const handleSubmitCreate = async (e) => {
        e.preventDefault();

        if(!post.title || !post.body) {
            setError('Why do you want to send an empty post? Write something!');
            return;
        }

        await axios({
            method: 'post',
            url: `${BASE_URL}`,
            data: post,
        });

        setPost({ id: 0, title: '', body: ''});
    };

    const handleTitle = (event) => {
        const id = v4().substring(0, 6);
        setError('');
        setPost({
            ...post,
            id: id,
            title: event.target.value,
        });
    };

    const handleBody = (event) => {
        setError('');
        setPost({
            ...post,
            body: event.target.value,
        });
    }

    return (
        <Layout>
           <s.Title>Create new post now.</s.Title>
            <s.Item>
                <s.ItemWrapper>
                    <form action="#" onSubmit={handleSubmitCreate}>
                        {error && <p>{error}</p>}
                        <label htmlFor="title">
                            <input
                                placeholder="Input Title of Post"
                                id="title"
                                type="text"
                                value={post.title}
                                onChange={handleTitle}
                            />
                        </label>
                        <label htmlFor="body">
                    <textarea
                        placeholder="Input Body of Post"
                        id="body"
                        value={post.body}
                        onChange={handleBody}
                    />
                        </label>
                        <button type="submit">Create post</button>
                    </form>
                </s.ItemWrapper>
            </s.Item>
        </Layout>
    );
}
