import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { List, PostBody } from './PostListStyles';
import { Card } from '../Card';
import { Modal } from '../Modal';
import { Form } from '../Form';
import { Buttons } from '../Buttons';
import { BASE_URL } from '../../utils/constants';

export const PostList = ({ postList, onChangedPosts }) => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [currentPost, setCurrentPost] = useState(null);

  const handleClickDelete = async (id) => {
    await axios.delete(`${BASE_URL}/${id}`).then(() => {
      onChangedPosts(postList.filter((post) => post.id !== id));
    });
    await axios.get(BASE_URL).then(({ data }) => {
      onChangedPosts(data);
    })

  };

  const handleClickEdit = (id) => {
    setIsModalOpen(true);
    setCurrentPost(postList.find((post) => post.id === id));
  };

  const editPost = async (post) => {
    await axios({
      method: 'PUT',
      url: `${BASE_URL}/${currentPost.id}`,
      data: post,
    }).then(() => {
      onChangedPosts(
        postList.map((item) => {
          if (item.id === post.id) {
            return post;
          }

          return item;
        }),
      );
      setIsModalOpen(false);
      setCurrentPost(null);
    });
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  return (
    <>
      <List>
        {postList.map(({ id, title, body }) => (
          <Card key={id} title={title} id={id}>
            <Buttons
              onEdit={() => handleClickEdit(id)}
              onDelete={() => handleClickDelete(id)}
            />
            <PostBody>{body}</PostBody>
          </Card>
        ))}
      </List>
      {isModalOpen && (
        <Modal onClose={closeModal} title={currentPost.title}>
          <Form onSubmitForm={editPost} currentPost={currentPost} />
        </Modal>
      )}
    </>
  );
};
