import axios from 'axios';

export const getPosts = async <T>(url: string): Promise<T> => {
  try {
    const response = await axios.get(url);

    if (!response.ok) {
        throw new Error(response.statusText);
      }

    return response;

  } catch (error) {
    return error;
  }
}
