import React, { useEffect, useState } from 'react';
import { List, PostBody } from "./PostListStyles";
import { Card } from "../Card";
import { Modal } from "../Modal";
import { Form } from "../Form";
import { Buttons } from "../Buttons";
import axios from "axios";
import {BASE_URL} from "../../utils/constants";

export const PostList = ({ postList, onChangedPosts }) => {
    const [posts, setPosts] = useState(postList);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [currentPost, setCurrentPost] = useState(null);

    const handleClickDelete = async (id) => {
        await axios.delete(`${BASE_URL}/${id}`).then(() => {
            onChangedPosts(posts.filter((post) => post.id !== id));
        });
    };

    const handleClickEdit = (id) => {
        setIsModalOpen(true);
        setCurrentPost(posts.find((post) => post.id === id));
    };

    const editPost =  async (post) => {
        await axios({
            method: 'PUT',
            url: `${BASE_URL}/${currentPost.id}`,
            data: post,
        }).then(() => {
            onChangedPosts(postList.map(item => {
                if(item.id === post.id){
                    return post;
                }

                return item;
            }));
            setPosts(postList);
            setIsModalOpen(false);
            setCurrentPost(null);
        });
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };

    return (
        <>
            <List>
                {posts.map(({ id, title, body }) => (
                    <Card key={id} title={title} id={id}>
                        <Buttons
                            onEdit={() => handleClickEdit(id)}
                            onDelete={() => handleClickDelete(id)}
                        />
                        <PostBody>{body}</PostBody>
                    </Card>
                ))}
            </List>
            {isModalOpen && (
                <Modal onClose={closeModal} title={currentPost.title}>
                    <Form onSubmitForm={editPost} currentPost={currentPost}/>
                </Modal>
            )}
        </>

    );
};
