import React, { useState } from 'react';
import { v4 } from 'uuid';
import axios from 'axios';
import { Layout  } from '../components/Layout';

const BASE_URL ='https://simple-blog-api.crew.red/posts';

export default function New() {
    const[post, setPost] = useState({ id: 0, title: '', body: ''})

    const handleSubmitCreate = (e) => {
        e.preventDefault();

        const id = v4().substring(0, 4);
        axios({
            method: 'post',
            url: `${BASE_URL}`,
            data: {
                id: id,
                title: post.title,
                body: post.body,
            }
        });
    };

    const handleTitle = (event) => {
        setPost({
            ...post,
            title: event.target.value,
        });
    };

    const handleBody = (event) => {
        setPost({
            ...post,
            body: event.target.value,
        });
    }

    return (
        <Layout>
           <p>Create new post now.</p>
            <form action="#" onSubmit={handleSubmitCreate}>
                <label htmlFor="title">
                    <input
                        placeholder="Input Title of Post"
                        id="title"
                        type="text"
                        value={post.title}
                        onChange={handleTitle}
                    />
                </label>
                <label htmlFor="body">
                    <textarea
                        placeholder="Input Body of Post"
                        id="body"
                        value={post.body}
                        onChange={handleBody}
                    />
                </label>
                <button type="submit">Create post</button>
            </form>
        </Layout>
    );
}
