import React, { FC } from 'react';
import PostLink from 'next/link';
import { ItemWrapper, Item, Title } from "./CardStyles";

interface Props {
  title?: string;
  children?: any;
  isLink: boolean;
  id?: string | number;
}

export const Card: FC<Props> = ({ title, isLink, children, id}) => (
  <Item>
      <ItemWrapper>
          {isLink
          ?
              <Title>
                  <PostLink id={id}>
                      <a title={title}>{title}</a>
                  </PostLink>
             </Title>
          :
             <Title>{title}</Title>
          }
          { children }
    </ItemWrapper>
  </Item>
);
