import React, { useEffect, useState } from 'react';
import axios  from 'axios';
import { Layout } from '../components/Layout'
import { s } from './styles/PostStyles';

const BASE_URL ='https://simple-blog-api.crew.red/posts'

export default function Posts() {
    const [ posts, setPosts ] = useState ([]);

    useEffect(() => {
        axios.get(BASE_URL)
            .then(function (response) {
                const postList = response.data.reverse();
                setPosts(postList);
            })
            .catch(function (error) {
                console.log(error);
            })
            .finally(function () {

            });
    }, []);

    const handleClickEdit = (id) => {
        console.log(id)
    };

    const handleClickDelete = (id) => {
        axios.delete(`${BASE_URL}/${id}`);
        setPosts(posts.filter(post => post.id !== id));
    };

    return (
        <Layout>
            <s.List>
                {posts.map(post => (
                    <s.Item key={`${post.id}-${post.title} `}>
                        <s.ItemWrapper >
                            <div>
                                <s.Title>
                                    <a title={post.title}>{post.title}</a>
                                </s.Title>
                                <button type="button" onClick={handleClickEdit}className="btn-edit">
                                    Edit
                                </button>
                                <button type="button" onClick={() => handleClickDelete(post.id)}className="btn-delete">
                                    Delete
                                </button>
                            </div>
                            <s.PostBody>{post.body}</s.PostBody>
                        </s.ItemWrapper>
                    </s.Item>
                ))}
            </s.List>
        </Layout>
    );
}
