import React, { FC } from 'react';
import PostLink from 'next/link';
import { ItemWrapper, Item, Title } from "./CardStyles";

interface Props {
  title?: string;
  children?: any;
  isLink: boolean;
  id?: any;
}

export const Card: FC<Props> = ({ title, isLink, id, children}) => (
  <Item>
      <ItemWrapper>
          {isLink
          ?
              <Title>
                  {
                      // @ts-ignore
                  <PostLink id={id ? id.toString() : '0'}>
                      <a title={title}>{title}</a>
                  </PostLink>
                  }
             </Title>
          :
             <Title>{title}</Title>
          }
          { children }
    </ItemWrapper>
  </Item>
);
