import styled from 'styled-components';

export const s = {
    Item: styled.div `
        max-width: 100%;
        position: relative;
        box-sizing: border-box;
        margin: 0;
    
    &:after,
    &:before {
        content: "";
        position: absolute;
        bottom: 18px;
        left: 5px;
        width: 50%;
        height: 30px;
        z-index: 0;
        max-width: 300px;
        box-shadow: 0 20px 5px rgba(0, 0, 0, 0.3);
        transform: rotate(-3deg);
        }
        
    &:after {
        right: 5px;
        left: auto;
        transform: rotate(3deg);
        }
`,

    ItemWrapper: styled.div `
        box-sizing: border-box;
        width: 100%;
        height: 100%;
        background: white;
        padding: 20px;
        box-shadow: 0 0 70px rgba(0, 0, 0, 0.1) inset;
        position: relative;
        z-index: 1;
        overflow: hidden;
        text-overflow: ellipsis;
    `,

    List: styled.div `
        display: grid;
        grid-template-columns: repeat(2, 50%);
        grid-gap: 40px;
    `,
};
