import styled from 'styled-components';

export const s = {
    Item: styled.div `
    flex-basis: 49%;
    width: 49%;
    position: relative;
    box-sizing: border-box;
    margin: 0;
    margin-bottom: 26px;
    
    &:after, 
    &:before {
        content: "";
        position: absolute;
        bottom: 18px;
        left: 5px;
        width: 50%;
        height: 30px;
        z-index: 0;
        max-width: 300px;
        box-shadow: 0 20px 5px rgba(0, 0, 0, 0.7);
        transform: rotate(-4deg);
        }
`,
};
