import styled from 'styled-components';

export const modal = {
    Backdrop: styled.div `
        position: fixed;
        left: 0;
        right: 0;
        top: 0;
        bottom: 0;
        z-index: 999;
        background-color: rgba(240, 246, 252, 0.5) ;
        backdrop-filter: blur(4px);
        display: flex;
        align-items: center;
        justify-content: center;
`,
    Wrapper: styled.div`
        min-width: 400px;
        padding-top: 16px;
        position: relative;
        background:  #f3969a;
    
        @media (max-width: 600px) {
          min-width: 100%;
        }
    `,
    Content: styled.div`
        box-shadow: 0 2px 4px rgba(100, 100, 100, 0.5);
        padding-bottom: 25px;
    `,
    BtnClose: styled.a`
        position: absolute;
        top: 0;
        right: 20px;
        background-color: $main-light;
        border-radius: 50%;
        border: 1px solid #d9d9d9;
        padding: 0;
        width: 40px;
        height: 40px;
        display: flex;
        align-items: center;
        justify-content: center;
        
        & svg {
            fill: #f3969a;
            width: 22px;
            height: 22px;
        }
        
    
        &:hover {
          background: #f3969a;
          border: 1px solid #df5c44;
          box-shadow: 0 2px 6px 0 $main-light;
    
          & svg {
            fill: #fff;
            width: 22px;
            height: 22px;
          }
        }
    `,
};
