import React from 'react';
import { v4 } from 'uuid';
import axios from 'axios';
import { Layout  } from '../components/Layout';

const BASE_URL ='https://simple-blog-api.crew.red/posts';

const handleClickCreate = () => {
    const id = v4().substring(0, 4);
    axios({
        method: 'post',
        url: `${BASE_URL}`,
        data: {
            id: id,
            title: 'title',
            body: 'Flintstone',
        }
    });
}

export default function New() {
    return (
        <Layout>
           <p>Create new post now.</p>

            <button type="button" onClick={handleClickCreate}>Create post</button>
        </Layout>
    );
}
