export const SET_TODOS = 'SET_TODOS';
export const SET_IS_LOADING = 'SET_IS_LOADING';
export const SORT_BY_TITLE = 'SORT_BY_TITLE';
export const SORT_BY_ID = 'SORT_BY_ID';
export const SORT_BY_USER = 'SORT_BY_USER';
export const SORT_BY_COMPLETED = 'SORT_BY_COMPLETED';
export const SET_IS_LOADED = 'SET_IS_LOADED';
export const REMOVE_TODO = 'REMOVE_TODO';



export const setTodos = (todos: PreparedTodo[]) => ({ type: SET_TODOS, todos });
export const sortByTitle = (todos: PreparedTodo[]) => ({ type: SORT_BY_TITLE, todos });
export const sortById = (todos: PreparedTodo[]) => ({ type: SORT_BY_ID, todos });
export const sortByUser = (todos: PreparedTodo[]) => ({ type: SORT_BY_USER, todos });
export const sortByCompleted = (todos: PreparedTodo[]) => ({ type: SORT_BY_COMPLETED, todos });
export const setIsLoading = () => ({ type: SET_IS_LOADING });
export const setIsLoaded = () => ({ type: SET_IS_LOADED });
export const removeTodo = (id: number) => ({ type: REMOVE_TODO, id });
