import React, { useEffect, useState } from 'react';
import axios  from 'axios';
import { Layout } from '../components/Layout'
import { s } from'./PostStyles';

const BASE_URL ='https://simple-blog-api.crew.red/posts'

export default function Posts() {
    const [ posts, setPosts ] = useState ([]);

    useEffect(() => {
        axios.get(BASE_URL)
            .then(function (response) {
                const postList = response.data.reverse();
                setPosts(postList);
            })
            .catch(function (error) {
                console.log(error);
            })
            .finally(function () {

            });
    }, []);

    const handleClickEdit = (id) => {
        console.log(id)
    };

    const handleClickDelete = (id) => {
        axios.delete(`${BASE_URL}/${id}`);
        setPosts(posts.filter(post => post.id !== id));
    };

    return (
        <s.ListWrapper>
            <Layout>
                <s.List>
                    {posts.map(post => (
                        <s.Item key={`${post.id}-${post.title} `}>
                            <s.ItemWrapper className="card">
                                <div className="card-header">
                                    <a title={post.title} className="card-title">{`#${post.id} ${post.title}`}</a>
                                    <button type="button" onClick={handleClickEdit}className="btn-edit">
                                        Edit
                                    </button>
                                    <button type="button" onClick={() => handleClickDelete(post.id)}className="btn-delete">
                                        Delete
                                    </button>
                                </div>
                                <div className="card-body">{post.body}</div>
                            </s.ItemWrapper>
                        </s.Item>
                    ))}
                </s.List>
            </Layout>
        </s.ListWrapper>
    );
}
