import styled from 'styled-components';

export const modal = {
    Backdrop: styled.div `
        position: fixed;
        left: 0;
        right: 0;
        top: 0;
        bottom: 0;
        z-index: 999;
        background-color: rgba(240, 246, 252, 0.5) ;
        backdrop-filter: blur(4px);
        display: flex;
        align-items: center;
        justify-content: center;
`,
    Wrapper: styled.div`
        padding: 60px 20px;
        margin: 15px 0;
        list-style: none;
    `,
    Content: styled.div`
    
    `,
    BtnClose: styled.a`
        display: block;
        padding: 0.5rem 1rem;
        color: #fff;
        text-decoration: none;
        background-color: transparent;
    `,
};
