import React, { useState } from 'react';
import axios from 'axios';
import { Modal  } from '../components/Modal';
import { Layout  } from '../components/Layout';
import { Form  } from '../components/Form';
import { Card  } from '../components/Card';


import { Provider } from 'react-redux';
import { store } from '../store/store';
import { connect } from 'react-redux';
import { openModal } from '../store/actionCreators';

const BASE_URL ='https://simple-blog-api.crew.red/posts';

interface DispatchProps {
    openModal(): void;
}

interface StateProps {
    modal: boolean;
}

type Props = DispatchProps & StateProps;

function NewTemplate<Props>({ modal, openModal }) {
    const[currentTitle, setCurrentTitle] = useState('');

    const setNewPost = async (post) => {
        setCurrentTitle(post.title);
        console.log(openModal)
        openModal();
        await axios({
            method: 'post',
            url: `${BASE_URL}`,
            data: post,
        });
    };

    const closeModal = () => {
        // setIsModalOpen(false);
        setCurrentTitle('');
    };

    return (
      <Provider store={store}>
          <Layout>
              <Card title="Create new post now!">
                  <Form onSubmitForm={setNewPost} />
              </Card>
              {modal &&
              <Modal onClose={closeModal} title={currentTitle}>
                <p>Success!!!</p>
                <p>The post was successfully added!</p>
              </Modal>
              }
          </Layout>
      </Provider>
    );
}


const mapStateToProps = (state: StateProps) => {
    return {
        modal: state.modal,
    };
};

const New = connect<StateProps, DispatchProps, {}, any>(
    mapStateToProps,
    {
        openModal
    },
)(NewTemplate);

export default New;
