import styled from 'styled-components';

export const s = {
    Item: styled.div `
        max-width: 100%;
        position: relative;
        box-sizing: border-box;
        margin: 0;
    
    &:after,
    &:before {
        content: "";
        position: absolute;
        bottom: 18px;
        left: 5px;
        width: 50%;
        height: 30px;
        z-index: 0;
        max-width: 300px;
        box-shadow: 0 20px 5px rgba(0, 0, 0, 0.3);
        transform: rotate(-3deg);
        }
        
    &:after {
        right: 5px;
        left: auto;
        transform: rotate(3deg);
        }
    `,

    ItemWrapper: styled.div `
        box-sizing: border-box;
        width: 100%;
        height: 100%;
        background: white;
        padding: 20px;
        box-shadow: 0 0 70px rgba(0, 0, 0, 0.1) inset;
        position: relative;
        z-index: 1;
        overflow: hidden;
        text-overflow: ellipsis;
    `,

    List: styled.div `
        display: grid;
        grid-template-columns: repeat(2, calc(50% - 10px));
        grid-gap: 20px;
    `,

    Title: styled.h2 `
        font-family: "Comfortaa",sans-serif;
        text-align: center;
        line-height: 1.2;
        color: #f3969a;
        font-size: 20px;
        font-weight: 700;
        margin-bottom: 20px;
        position: relative;
        text-transform: uppercase;
        
        &:before {
            content: '';
            width: 100%;
            position: absolute;
            bottom: -10px;
            height: 1px;
            background: linear-gradient(to right, rgba(255,255,255,0) 0%,rgba(243, 150, 154,1) 49%, rgba(255,255,255,0) 100%);
            left: 0;
            right: 0;
            z-index: 1;
        }
    `,
};
