import styled from 'styled-components';

export const s = {
    Item: styled.div `
        flex-basis: 48%;
        width: 48%;
        position: relative;
        box-sizing: border-box;
        margin: 0;
        margin-bottom: 26px;
    
    &:after {
        content: "";
        position: absolute;
        bottom: 18px;
        left: 5px;
        width: 50%;
        height: 30px;
        z-index: 0;
        max-width: 300px;
        box-shadow: 0 20px 5px rgba(0, 0, 0, 0.7);
        transform: rotate(-4deg);
        }
    
    &:before {
        content: "";
        position: absolute;
        bottom: 18px;
        left: 5px;
        width: 50%;
        height: 30px;
        z-index: 0;
        max-width: 300px;
        box-shadow: 0 20px 5px rgba(0, 0, 0, 0.7);
        transform: rotate(-4deg);
        }
`,

    ItemWrapper: styled.div `
        width: 100%;
        height: 100%;
        background: floralwhite;
        padding: 20px 15px;
        box-shadow: 0 0 70px rgba(0, 0, 0, 0.1) inset;
        position: relative;
        z-index: 1;
    `,

    List: styled.div `
        display: grid;
        grid-template-columns: repeat(2, 1fr);
        grid-gap: 20px;
    `,
};
